import React, { useState, useRef } from "react";
import {
  Stack,
  Heading,
  Button,
  Container,
  Image,
  Flex,
  Box,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  Input,
  VStack,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  useToast,
  Text,
  Icon,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { useNavigate } from "react-router-dom";

// --- Configuration ---
const API_BASE_URL = 'http://127.0.0.1:8000'; // Ensure your backend is running at this address

// --- Custom SVG Icon for File Upload ---
const FileUploadIcon = (props) => (
  <Icon viewBox="0 0 24 24" {...props}>
    <path
      fill="currentColor"
      d="M19.35 10.04C18.67 6.59 15.64 4 12 4C9.11 4 6.6 5.64 5.35 8.04C2.34 8.36 0 10.91 0 14c0 3.31 2.69 6 6 6h13c2.76 0 5-2.24 5-5c0-2.64-2.05-4.78-4.65-4.96zM14 13v4h-4v-4H7l5-5 5 5h-3z"
    />
  </Icon>
);

// --- Authentication Modal Component ---
const AuthModal = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const toast = useToast();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [company, setCompany] = useState("");
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const fileInputRef = useRef();

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const uploadKnowledgeBase = async () => {
    const formData = new FormData();
    formData.append("file", file);
    const fileResponse = await fetch(`${API_BASE_URL}/api/v1/documents/upload-docs`, {
      method: "POST",
      body: formData,
    });
    if (!fileResponse.ok) {
      const errData = await fileResponse.json();
      throw new Error(errData.detail || "Failed to upload knowledge base.");
    }
  };

  const handleSignIn = async () => {
    if (!file) {
      setError("Please upload a CSV knowledge base file to sign in.");
      return;
    }
    setIsLoading(true);
    setError("");
    try {
      // Step 1: Verify user exists
      const response = await fetch(`${API_BASE_URL}/api/v1/crm/users/by_email?email=${encodeURIComponent(email)}`);
      if (!response.ok) {
        throw new Error("No account found with this email. Please sign up.");
      }
      const userData = await response.json();

      // Step 2: Upload the CSV file
      await uploadKnowledgeBase();

      toast({
        title: "Login Successful",
        description: `Welcome back, ${userData.name || userData.email}! Knowledge base updated.`,
        status: "success",
        duration: 3000,
        isClosable: true,
      });
      navigate("/user/dashboard/");
    } catch (err) {
      setError(err.message);
    }
    setIsLoading(false);
  };

  const handleSignUp = async () => {
    if (!file) {
      setError("Please upload a CSV knowledge base file to sign up.");
      return;
    }
    setIsLoading(true);
    setError("");

    try {
      // Step 1: Create the user
      const userResponse = await fetch(`${API_BASE_URL}/api/v1/crm/users`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, name, company }),
      });

      if (!userResponse.ok) {
        const errData = await userResponse.json();
        throw new Error(errData.detail || "Failed to create user.");
      }

      // Step 2: Upload the CSV file
      await uploadKnowledgeBase();

      toast({
        title: "Account Created & Knowledge Base Uploaded!",
        description: "You are now being redirected to your dashboard.",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
      navigate("/user/dashboard/");
    } catch (err) {
      setError(err.message);
    }
    setIsLoading(false);
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent bg="gray.800" color="white">
        <ModalHeader>Get Started</ModalHeader>
        <ModalCloseButton />
        <ModalBody>
          <Tabs isFitted variant="enclosed-colored" colorScheme="purple">
            <TabList>
              <Tab>Sign In</Tab>
              <Tab>Sign Up</Tab>
            </TabList>
            <TabPanels>
              {/* Sign In Panel */}
              <TabPanel>
                <VStack as="form" spacing={4} onSubmit={(e) => { e.preventDefault(); handleSignIn(); }}>
                  <FormControl isRequired>
                    <FormLabel>Email Address</FormLabel>
                    <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
                  </FormControl>
                  <FormControl isRequired>
                    <FormLabel>Upload Knowledge Base (CSV)</FormLabel>
                    <Button w="full" variant="outline" leftIcon={<FileUploadIcon />} onClick={() => fileInputRef.current.click()}>
                      {file ? file.name : "Select File"}
                    </Button>
                    <Input type="file" ref={fileInputRef} onChange={handleFileChange} accept=".csv" style={{ display: "none" }}/>
                  </FormControl>
                  <Button type="submit" colorScheme="purple" w="full" isLoading={isLoading}>
                    Sign In & Start
                  </Button>
                </VStack>
              </TabPanel>
              {/* Sign Up Panel */}
              <TabPanel>
                <VStack as="form" spacing={4} onSubmit={(e) => { e.preventDefault(); handleSignUp(); }}>
                  <FormControl isRequired>
                    <FormLabel>Name</FormLabel>
                    <Input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="John Doe" />
                  </FormControl>
                  <FormControl>
                    <FormLabel>Company (Optional)</FormLabel>
                    <Input type="text" value={company} onChange={(e) => setCompany(e.target.value)} placeholder="Example Inc." />
                  </FormControl>
                  <FormControl isRequired>
                    <FormLabel>Email Address</FormLabel>
                    <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
                  </FormControl>
                  <FormControl isRequired>
                    <FormLabel>Upload Knowledge Base (CSV)</FormLabel>
                    <Button w="full" variant="outline" leftIcon={<FileUploadIcon />} onClick={() => fileInputRef.current.click()}>
                      {file ? file.name : "Select File"}
                    </Button>
                    <Input type="file" ref={fileInputRef} onChange={handleFileChange} accept=".csv" style={{ display: "none" }}/>
                  </FormControl>
                  <Button type="submit" colorScheme="purple" w="full" isLoading={isLoading}>
                    Sign Up & Start
                  </Button>
                </VStack>
              </TabPanel>
            </TabPanels>
          </Tabs>
          {error && (
            <Alert status="error" mt={4} rounded="md">
              <AlertIcon />
              <Text>{error}</Text>
            </Alert>
          )}
        </ModalBody>
        <ModalFooter />
      </ModalContent>
    </Modal>
  );
};

